import React, { useState } from 'react'
import auth0 from '../lib/auth0'

const createStatus = () => {

    const [dados, setDados] = useState({
        status: 'Bem',
        coords: {
            lat: null,
            long: null
        }
    })

    const GetMyLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(position => {
                setDados(old => {
                    return {
                        ...old,
                        coords: {
                            lat: position.coords.latitude,
                            long: position.coords.longitude
                        }
                    }
                })
            })
        }
    }

    return (
        <div>
            <label><input type="radio" name='status' value='Bem' />Estou bem e sem sintomas</label>
            <label><input type="radio" name='status' value='Gripe' />Estou com sintomas de Gripe / Resfriado</label>
            <label><input type="radio" name='status' value='Covid' />Estou com sintomas de Coronga</label>
            Sua posição atual: {JSON.stringify(dados)}
            <button onClick={GetMyLocation}>Get My Location</button>
        </div>
    )
}

export default createStatus

export async function getServerSideProps({ req, res }) {
    const session = await auth0.getSession(req)
    if (session) {

        return {
            props: {
                isAuth: true,
                user: session.user
            }
        }
    }
    return {
        props: {
            isAuth: false,
            user: {}
        }
    }
}
